import React from "react";
import "./Table.css";
import { useSelector } from "react-redux";

const Table = () => {
  const sales = useSelector((state) =>  state.data[0].sales[0].sales);
  if (!sales || sales.length === 0) {
    return <p> Loading sales data...</p>;
  }
  return (
    <div className="table">
      <table>
        <thead>
          <tr>
            <th> Week Ending</th>
            <th> Retail Sales</th>
            <th> Wholesale Sales</th>
            <th> Units Sold</th>
            <th> Retailer Margin</th>
          </tr>
        </thead>
        <tbody>
          {sales.map((sale) => (
            <tr key={sale.weekEnding}>
              <td>{sales.weekEnding}</td>
              <td>{sales.retailSales.toLocaleString()}</td>
              <td>{sales.wholesaleSales.toLocaleString()}</td>
              <td>{sales.unitsSold}</td>
              <td>{sales.retailerMargin.toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
export default Table;
