import React from "react";
import "./SideBar.css";
import blender from "../../assets/ninja_blender.jpg";

const SideBar = () => {
    const tags = useSelector((state) => state.data.tags);
    return(
        <div className="sidebar">
            <img src={blender} alt="ninja blender"/>
            <h1>Shark Ninja</h1>
            <p>Magic Bullet NutriBullet 12-Piece High-Speed Blender/Mixer System</p>
            <div className="tags">
                {
                    tags.map( (tag)=>(
                     <button key={tag}>{tag}</button>   
                    ))
                }
                {sales.map((sale) => (
            <tr key={sale.weekEnding}>
              <td>{sale.weekEnding}</td>
              <td>${sale.retailSales.toLocaleString()}</td>
              <td>${sale.wholesaleSales.toLocaleString()}</td>
              <td>{sale.unitsSold}</td>
              <td>${sale.retailerMargin.toLocaleString()}</td>
            </tr>
          ))}
            </div>
        </div>
    )

};
export default SideBar;
